import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { FilterMatchMode, FilterOperator } from 'primereact/api';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import "primereact/resources/themes/lara-light-indigo/theme.css";
import 'primereact/resources/primereact.min.css';

const UsersTable = () => {
    const [users, setUsers] = useState([]);
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [searchText, setSearchText] = useState('');
    const [sortField, setSortField] = useState('');
    const [sortOrder, setSortOrder] = useState(1);
    const [currentPage, setCurrentPage] = useState(1);
    const [usersPerPage] = useState(5);
    const [filters, setFilters] = useState(null);
    const [globalFilterValue, setGlobalFilterValue] = useState('');
    const paginatorLeft = <Button type="button" icon="pi pi-refresh" text />;
    const paginatorRight = <Button type="button" icon="pi pi-download" text />;
    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch('http://localhost:2525/users');
            const data = await response.json();
            setUsers(data);
            setFilteredUsers(data);
        };

        fetchData();
    }, []);

    const handleSearch = (event) => {
        const searchText = event.target.value.toLowerCase();
        setSearchText(searchText);
        filterUsers(searchText); // Update this line
        setCurrentPage(1);
    };

    const handleSort = (event) => {
        const { field, order } = event.sort;
        setSortField(field);
        setSortOrder(order);
        filterUsers(searchText, field, order);
        setCurrentPage(1);
    };

    const filterUsers = (searchText) => {
        let filteredData = users.filter((user) =>
            user.name.toLowerCase().includes(searchText)
        );

        if (sortField) {
            filteredData.sort((a, b) => {
                const valueA = a[sortField].toLowerCase();
                const valueB = b[sortField].toLowerCase();
                if (valueA < valueB) {
                    return sortOrder === 1 ? -1 : 1;
                }
                if (valueA > valueB) {
                    return sortOrder === 1 ? 1 : -1;
                }
                return 0;
            });
        }

        setFilteredUsers(filteredData);
    };
    const clearFilter = () => {
        initFilters();
    };

    const onGlobalFilterChange = (e) => {
        const value = e.target.value;
        let _filters = { ...filters };

        _filters['global'].value = value;

        setFilters(_filters);
        setGlobalFilterValue(value);
    };

    const initFilters = () => {
        setFilters({
            global: { value: null, matchMode: FilterMatchMode.CONTAINS },
            name: { operator: FilterOperator.AND, constraints: [{ value: null, matchMode: FilterMatchMode.STARTS_WITH }] },
            'country.name': { operator: FilterOperator.AND, constraints: [{ value: null, matchMode: FilterMatchMode.STARTS_WITH }] },
            representative: { value: null, matchMode: FilterMatchMode.IN },
            date: { operator: FilterOperator.AND, constraints: [{ value: null, matchMode: FilterMatchMode.DATE_IS }] },
            balance: { operator: FilterOperator.AND, constraints: [{ value: null, matchMode: FilterMatchMode.EQUALS }] },
            status: { operator: FilterOperator.OR, constraints: [{ value: null, matchMode: FilterMatchMode.EQUALS }] },
            activity: { value: null, matchMode: FilterMatchMode.BETWEEN },
            verified: { value: null, matchMode: FilterMatchMode.EQUALS }
        });
        setGlobalFilterValue('');
    };

    return (
        <div className="users-table">
            {/* <input
                type="text"
                value={searchText}
                onChange={handleSearch}
                placeholder="Search..."
            /> */}
   <div className="flex justify-content-between">

                <span className="p-input-icon-left">
                    <i className="pi pi-search" />
                    <InputText value={globalFilterValue} onChange={onGlobalFilterChange} placeholder="Keyword Search" />
                </span>
            </div>
            <DataTable
                value={filteredUsers} // Use currentUsers instead of filteredUsers
                paginator
                rows={5}
                rowsPerPageOptions={[5, 10, 25, 50]}
                tableStyle={{ minWidth: '50rem' }}
                paginatorTemplate="RowsPerPageDropdown FirstPageLink PrevPageLink CurrentPageReport NextPageLink LastPageLink"
                currentPageReportTemplate="{first} to {last} of {totalRecords}"
                paginatorLeft={paginatorLeft}
                paginatorRight={paginatorRight}
                filters={filters} globalFilterFields={['name', 'country.name', 'representative.name', 'balance', 'status']}
            >
                <Column
                    sortable
                    style={{ width: '10%' }}
                    field="id"
                    header="ID"
                    sortField="id"
                    sortOrder={sortField === 'id' ? sortOrder : null}
                    onSort={handleSort}
                ></Column>
                <Column
                    sortable
                    style={{ width: '30%' }}
                    field="username"
                    header="Username"
                    sortField="username"
                    sortOrder={sortField === 'username' ? sortOrder : null}
                    onSort={handleSort}
                ></Column>
                <Column
                    sortable style={{ width: '10%' }}
                    field="name"
                    header="Name"
                    sortField="name"
                    sortOrder={sortField === 'name' ? sortOrder : null}
                    onSort={handleSort}
                ></Column>
                <Column
                    sortable style={{ width: '15%' }}
                    field="email"
                    header="Email"
                    sortField="email"
                    sortOrder={sortField === 'email' ? sortOrder : null}
                    onSort={handleSort}
                ></Column>
                <Column
                    sortable style={{ width: '10%' }}
                    field="status"
                    header="Status"
                // body={(rowData) => (rowData.active ? 'Active' : 'Inactive')}
                ></Column>
            </DataTable>
        </div>
    );
};

export default UsersTable;
